    constructor(props){
        super(props)
        this.tid = this.props.tid
        this.teamId = this.props.teamId
        this.userID = this.props.userID

        this.state = {
            teamId: this.teamId,
            tid: this.tid,
            hasTeam: false,
        }

    }

    componentDidMount(){
        this.checkname()



    }

    checkname(){

        if(this.state.teamId == undefined){
            firebase.database().ref('futureTourneys/' + this.tid + '/Players/' + this.userID).on('value', (data) =>
            {

