        }
    }

    render() {
        return (
            <div id="newContainer">
                {
                    this.state.hasTeam ?
                    <CreateTeam tid={this.state.tid} />

                    :
                    <div id="myTeamContainer">











                    </div>

                }

            </div>
        );
    }
}

export default myTeam;
