import React, { Component } from 'react';
import '../../SCSS/login.scss'
import ngeLogo from '../Images/nge.png'
import * as firebase from 'firebase';



class login extends Component {

    constructor(props){
        super(props);
        this.state={
            username: "",
            num: false,
        }
        this.signUp = this.signUp.bind(this);
        this.signIn = this.signIn.bind(this);
        this.ajaxCalls = this.ajaxCalls.bind(this);

        this.username = "";
        this.password1 = "";
        this.password2 = "";
        this.email = "";
        this.userID = "";
        this.loginEmail = "";
        this.loginPassword = "";
    }

    signIn(e){

    }

    signUp(e){
       if(this.state.password1 == this.state.password2)
       {
               var u = this.usernameCheck
               alert(u)
               if(u == true)
               {
                   var e = this.emailCheck
                   if(e == true)
                   {
                        firebase.auth().createUserWithEmailAndPassword(this.email, this.password).then(function(user){

                           var userData = {
                             username: this.username,
                               email: this.email,
                              userID: firebase.auth().currentUser.uid
                         }
                    this.ajaxCalls("Post", "createUser", userData)
                     })
                   }
                   else{
                       alert("Email already used")
                   }
               }
               else{
                   alert("Username already exist")
               }









       }
       else{
           alert("Passwords do not match")
       }

    }

     ajaxCalls(type, url, data){

             url = 'http://127.0.0.1:5000/' + url
             fetch(url, {
                 method: 'POST',
                 headers:{
                     'Accept': 'application/json',
                     'Content-Type': 'application/json',
                 },
                 body: JSON.stringify({
                    data,
                 })
             })
             .then(res => res.json())
             .then(responce => {
                 return(responce)
             })

        }

        checkEmail(){
            var emailData={
                email: this.email,
            }
            var emailCheck = this.ajaxCalls("Post", "checkEmail", emailData)
            alert(emailCheck)
            if(emailCheck == false){
                return true
            }
            else{
                return false
            }
        }
        checkUsername(){
            var usernameData = {
                username: this.username,
            }

            var usernameCheck = this.ajaxCalls("Post", "checkUsername", usernameData)
            alert(usernameCheck)
            if(usernameCheck == false){
                return true;
            }
            else{
                return false
            }

        }




    render() {
        return (
            <div id="loginMainPanel">
                <div id="leftPanel">
                    <img src={ngeLogo} id="ngeLogo" />

                </div>

                <div id="middlePanel">
                    <div id="registerPanel">

                        <input ref={this.username} onChange={e => 
                        this.setState({username: e.target.value})} placeholder="Username"/><br />

                        <input ref={this.email} onChange={e => 
                        this.setState({email: e.target.value})}placeholder="Email"/><br />

                        <input ref={this.password1} onChange={e => 
                        this.setState({password1: e.target.value})} placeholder="Password"/><br />

                        <input ref={this.password2} onChange={e => 
                        this.setState({password2: e.target.value})}placeholder="Re-Password"/><br />

                        <button onClick={this.signUp}> Sign Up </button>

                    </div>

                    <div id="loginPanel">


                        <input ref={this.loginEmail} onChange={e => 
                        this.setState({loginEmail: e.target.value})}placeholder="Email"/><br />

                        <input ref={this.loginPassword} onChange={e => 
                        this.setState({loginPassword: e.target.value})} placeholder="Password"/><br />



                        <button onClick={this.signUp}> Sign In </button>

                    </div>

                </div>


            </div>
        );
    }
}

export default login;
