import React, { Component } from 'react';
import * as firebase from 'firebase';


class tournteam extends Component {
    constructor(props){
        super(props)
        this.state={
            userID: '',
            tid: '',
            teamID: '',
            captain: false,
        }
        this.state.tid = this.props.tid
        this.state.teamID = this.teamID

    }

    componentDidUpdate(prevProps){
        if (this.props.teamID !== prevProps.teamID) {
           firebase.database().ref('tourneys/' + this.state.tid + '/captain/').once('value', (data) => {
               if(data.toJSON() == this.state.userID){
                   this.setState({
                       captain: true,
                   })
               }
           })
        }

    }


    render() {
        return (
            <div>
                {
                    this.state.captain ?
                    <div id="captainContainer">
                        <div id="rosterPanel">



                        </div>









                    </div>
                    :
                    <div id="playerContainer">

                    </div>




                }
            </div>
        );
    }
}

export default tournteam;
