    constructor(props){
        super(props)
        this.tid = this.props.tid
        this.teamId = this.props.teamId
        this.userID = this.props.userID

        this.state = {
            teamId: this.teamId,
            tid: this.tid,
            userID: this.userID,
            hasTeam: true,
        }

    }

