import {withRouter} from 'react-router';
import { NavLink, BrowserRouter, Route, Switch, Link } from "react-router-dom"

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      user: {},
    }

    this.username = ""
    this.imageFile = ""
  }

