            alert(this.state.userID)
            if(this.state.userID === firebase.auth().currentUser.uid)
            {

                if(this.state.teamId == undefined){
                    firebase.database().ref('futureTourneys/' + this.tid + '/Players/' + this.state.userID).once('value', (data) =>
                    {

                      console.log(data)
                      if(data.toJSON() == undefined){

                      }
                      else{

                          this.setState({
                            hasTeam: false,
                            teamId: data.toJSON(),
                        })
                      }                        
                    })


                }


            }

        }
    }
    checkname(){



    }

    render() {
        return (
            <div id="newContainer">
                {
                    this.state.hasTeam ?
                    <CreateTeam tid={this.state.tid} />
                    :

                    <MyTournTeam teamId={this.state.teamId} tid={this.state.tid} userID={this.state.userID} />
                }

            </div>
        );
    }
}

export default myTeam;
