import React, { Component } from 'react';
import ngeLogo from '../Images/nge.png'
import {withRouter} from 'react-router'
import '../../SCSS/tournaments.scss'
import cod from '../Images/cod.jpg'
import fortnite from '../Images/fortnite.jpeg'
import tk from '../Images/tk.jpeg'
import madden from '../Images/madden.png'

import * as firebase from 'firebase';



class tournament extends Component {
    constructor(props){
        super(props)
        this.state = {
            numberofTop: [],
            numberofBottom: [],
        }
    }

    componentDidMount(){
        const num = firebase.database().ref().child('numbers');
        num.once('value', snapshot =>{
            snapshot.forEach(child =>{

                this.setState({
                    numberofTop: this.state.numberofTop.concat([child.val]),
                    numberofBottom: this.state.numberofBottom.concat([child.val])
                })
            })
        })
    }

    random(){

    }
    render() {
        return (
            <div id="TournamentContainer">
                <div id="topRow">
                    <table>
                    <th>
                            <img src={cod} id="codId" />

                        </th>
                        <th>
                            <img src={fortnite} id="fortniteId" />

                        </th>
                        <th>
                            <img src={tk} id="2kId" />

                        </th>
                        <th>
                            <img src={madden} id="maddenId" />

                        </th>
                    </table>

                </div>

                <div id="middleRow">
                    <table id="topRowTable">
                        {this.state.numberofTop.map((data, i) =>{
                            return(

                                <th>
                                    <img src={cod} id="imageTag" />
                                    <div id="info"> 
                                    <div id="title">
                                         Duos Tournament
                                    </div>
                                    <div id="Credits">
                                        5 Credits to enter
                                    </div>
                                    <div id="payout">
                                        85% payout
                                    </div>
                                    <div id="time">
                                        3:30 PM Jan 5
                                    </div>
                                    </div>
                                </th>

                            )
                        })}
                    </table>

                </div>
                <div id="bottomRow">
                <table id="topRowTable">
                        {this.state.numberofTop.map((data, i) =>{
                            return(

                                <th>
                                    <img src={cod} id="imageTag" />
                                    <div id="info"> 
                                    <div id="title">
                                         Duos Tournament
                                    </div>
                                    <div id="Credits">
                                        5 Credits to enter
                                    </div>
                                    <div id="payout">
                                        85% payout
                                    </div>
                                    <div id="time">
                                        3:30 PM Jan 5
                                    </div>
                                    </div>
                                </th>

                            )
                        })}
                    </table>

                </div>
            </div>
        );
    }
}

export default withRouter(tournament);
