import React, { Component } from 'react';
import '../../SCSS/tid.scss'
import cod from '../Images/cod.jpg'
import MyTeamPage from '../assigned/myTeam.js'


class tournamentId extends Component {
    constructor(props){
        this.userID = firebase.auth().currentUser.uid
        super(props)
        this.tid = this.props.match.params.id
        this.userTeamId = ''
        this.state = {
            bracket: false,
            myTeam: true,
            rules: false,
        }
    }

    componentDidMount(){
        firebase.database().ref('futureTourneys/' + this.tid + '/Players/' + this.userID).once('value', (data) =>
        {
            this.userTeamId = data.toJSON()
            if(this.userTeamId == ''){
                firebase.database().ref('currentTourneys/' + this.tid + '/Players/' + this.userID).once('value', (data) =>
                {
                    this.userTeamId = data.toJSON()                         
                })
            }

        })

    }

    controlComponent(d){

        switch(d){
            case 'bracket':
                this.setState({
                    bracket: true,
                    myTeam: false,
                    rules: false,
                })
                break;

                case 'myTeam':
                this.setState({
                    bracket: false,
                    myTeam: true,
                    rules: false,
                })
                break;

                case 'rules':
                this.setState({
                    bracket: false,
                    myTeam: false,
                    rules: true,
                })
                break;
        }

    }
    render() {
        return (
            <div id="tContainer">
                <div id="tMenu">
                    <div id="ttMenu">
                         <div class="tMen" onClick={()=>{this.controlComponent('bracket')}}>Bracket</div>
                         <div class="tMen" onClick={()=>{this.controlComponent('myTeam')}}>MyTeam</div>
                         <div class="tMen" onClick={()=>{this.controlComponent('rules')}}>Rules</div>
                    </div>
                    <div>
                    {
                        this.state.bracket ?
                        <bracket tid = {this.tid} />
                        : null

                    }

                    {
                        this.state.myTeam ?
                        <MyTeamPage tid={this.tid} teamid={this.teamId} />
                        : null
                    }

                    {
                        this.state.rules ?
                        <rules tid={this.tid} />
                        : null
                    }

                  </div>

                </div>

            </div>
        );
    }
}

export default tournamentId;
