import React, { Component } from 'react';
import '../../SCSS/myTeam.scss'
import CreateTeam from '../assigned/createTeam.js'
import * as firebase from 'firebase';
import MyTournTeam from '../assigned/tournteam.js'

class myTeam extends Component {
    constructor(props){
        super(props)
        this.state = {
            teamId: this.teamId,
            tid: this.tid,
            userID: this.userID,
            hasTeam: true,
        }
        this.tid = this.props.tid
        this.teamId = this.props.teamId
        this.userID = this.props.userID



    }

    componentDidMount(){
        this.checkname()



    }

    checkname(){

        if(this.state.teamId == undefined){
            firebase.database().ref('futureTourneys/' + this.tid + '/Players/' + this.userID).on('value', (data) =>
            {

              if(data.toJSON() == undefined){

              }
              else{
                  this.state.teamId = data.toJSON()
                  this.setState({
                    hasTeam: false,
                })
              }                        
            })


        }
    }

    render() {
        return (
            <div id="newContainer">
                {
                    this.state.hasTeam ?
                    <CreateTeam tid={this.state.tid} />
                    :
                    <MyTournTeam teamId={this.state.teamId} tid={this.state.tid} userID={this.state.userID} />
                }

            </div>
        );
    }
}

export default myTeam;
